mapper.select:
select receive
from message
where mno=#{mno}

select count(*)
from message
where receive=#{receive}

select *
from (
	select rownum as rn,A.*
	from (
		select mno,text,send,regdate
		from message
		where receive=#{receive} and isview='n'
		order by mno desc
	) A
) where rn between 1 and 3

mapper.update:
update message
set isview='y'
where mno in (select mno
			  from (
			  	select rownum as rn,A.*
			  	from (
			  		select mno
			  		from message
			  		where receive=#{receive} and isview='n'
			  		order by mno desc
			  	) A
			  ) where rn between 1 and 3
			 )

