mapper.select:
select *
from (
	select rownum as rn,A.*
	from (
		select rno,bno,replyer,replytext,regdate
		from reply
		where bno=#{bno}
		order by rno asc
	) A
) where rn between #{start} and #{end}

select count(*)
from reply
where bno=#{bno}

select replyer
from reply
where rno=#{rno}

mapper.update:
update reply
set replytext=#{replytext},updatedate=sysdate
where rno=#{rno}
